// Initialize the Windy API
windyInit = function () {
	windyInit({
		key: 'NeY8acCr7T906bbnnfWkjWWFUr1lRiRF', // Replace with your API key
		lat: 37.7749,
		lon: -122.4194,
		zoom: 5,
		// Add any other Windy options you want
	}).then(function (windy) {
		const { map } = windy;

		// Function to render lakes in color
		function renderLakes() {
			// You may need to adjust the parameters based on your data
			const lakesGeoJSON = fetchLakesData(); // Assume this function fetches the lakes data

			// Adding lakes to the map with color
			const lakesLayer = new L.GeoJSON(
				lakesGeoJSON,
				{
					style: {
						fillColor: '#00f', // Color for lakes
						color: '#00f', // Border color for lakes
						weight: 1,
						opacity: 0.7,
						fillOpacity: 0.7,
					},
				},
			);

			lakesLayer.addTo(map);
		}

		// Call the function to render lakes
		renderLakes();

		// Apply black and white filter to the rest of the map
		const blackAndWhiteLayer = L.tileLayer(
			'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
			{
				// Black and white filter can be applied via CSS or a map style
				className: 'black-and-white',
			},
		);

		blackAndWhiteLayer.addTo(map);
	});
};

// CSS for black and white filter
const style = document.createElement('style');
style.innerHTML = `
  .black-and-white {
    filter: grayscale(100%) contrast(100%);
  }
`;
document.head.appendChild(style);
